/**
 * @file Since SpeechKit's whole SDK needs to be loaded to use the audio player,
 * and the SDK is +400kb, we initially just show a button on the article page.
 * When that button is clicked, the SDK is downloaded, and the player
 * rendered and started.
 * @see https://docs.speechkit.io/9b973c9db35a4487b8a19bfaabfb0a6d
 */

(function (Drupal, FontAwesome) {

  'use strict';

  Drupal.behaviors.speechkitPlayerInit = {
    attach: function(context, settings) {
      const button = document.getElementById('speechkit-init');

      const spinner = FontAwesome.icon({
        prefix: 'far',
        iconName: 'spinner-third'
      }).node[0];


      button.addEventListener('click', function () {
        const play = button.getElementsByClassName('button-icon')[0].getElementsByClassName('fa-play-circle')[0];
        button.setAttribute('disabled', 'disabled');
        button.getElementsByClassName('button-text')[0].innerText = 'Loading...';
        // button.getElementsByClassName('button-icon')[0].replaceChild(spinner, play);
        play.replaceWith(spinner);
        const script = document.createElement('script');
        script.type = 'text/javascript';
        script.src = settings.speechkit.sdk_path;
        document.body.appendChild(script);
      });
    }
  };

})(Drupal, FontAwesome);

