/**
 * @file
 * Piano ID login
 * @see https://docs.piano.io/piano-id-overview/#pianoidjs
 */
Drupal.behaviors.pianoIdLogin = {
  attach: function (context, settings) {
    'use strict';

    jQuery('.js-piano-login', context).on('click', function() {
      showLogin();
    });

    if (window.location.search.match(/show_piano_login/)) {
      showLogin();
    }

    function showLogin() {
      window['tp'] = window['tp'] || [];
      window['tp'].push(['init', function() {
        window['tp'].pianoId.show({
          displayMode: 'modal',
          screen: 'login'
        });
      }]);
    }

  }
};

