/**
 * @file
 * Piano ID logout
 * @see https://docs.piano.io/piano-id-overview/#pianoidjs
 */
Drupal.behaviors.pianoIdLogout = {
  attach: function (context, settings) {
    'use strict';

    window['tp'] = window['tp'] || [];

    // Redirect user to front page on logout
    window['tp'].push(['init', function() {
      window['tp'].pianoId.init({
        // There is no `logoutSuccess` callback, so reacting to log out event here.
        loggedOut: function () {
          window.location.replace('/front');
        }
      });
    }]);

    // Log out user when they click log out link
    jQuery('.js-piano-logout', context).on('click', function () {
      window['tp'].pianoId.logout();
    });
  }
};

