/**
 * @file
 * Handles event fired from newsletter popup in Piano Composer.
 */

Drupal.behaviors.pianoNewsletter = {
  attach: function (context, settings) {
    'use strict';

    window['tp'] = window['tp'] || [];

    window['tp'].push(['init', function () {
      window['tp'].push(['addHandler', 'customEvent', function (event) {
        if (event.eventName === 'mc-signup') {
          var email = '';
          var fname = '';
          var lname = '';
          var phone = '';
          var signup = '';
          var params;
          var data = {};
          var iframeId;

          // We are parsing the params object sent from the template to find out which iframe triggered it
          params = JSON.parse(event.params.params);
          // And here's the iframeId we're looking for
          iframeId = params.iframeId;

          if ((typeof event.params.email !== 'undefined') && (event.params.email.length > 0)) {
            email = event.params.email;
            data.email = email;
          }
          if ((typeof event.params.fname !== 'undefined') && (event.params.fname.length > 0)) {
            fname = event.params.fname;
          }
          if ((typeof event.params.lname !== 'undefined') && (event.params.lname.length > 0)) {
            lname = event.params.lname;
          }
          if ((typeof event.params.phone !== 'undefined') && (event.params.phone.length > 0)) {
            phone = event.params.phone;
          }
          if ((typeof event.params.signup !== 'undefined') && (event.params.signup.length > 0)) {
            signup = event.params.signup;
          }

          data.lname = lname;
          data.fname = fname;
          data.phone = phone;
          data.signup = signup;

          jQuery.ajax({
            url: '/ajax/mc-subscribe',
            method: 'POST',
            data: jQuery.param(data),
            dataType: 'json'
          })
          .done(function (data, textStatus, jqXHR) {
            if (data.success === false) {
              sendPostMessageToPiano(iframeId, false, 'Something went wrong. Please try again.');
            }
            // Status depends on whether double opt-in is enabled or not in natobs_newsletter_mc_subscribe_json()
            else if (data.status === 'pending') {
              // Set a cookie we can use in Composer.
              Cookies.set('_pc_nbp', 'sub', {expires: 7365, path: '/'});
              sendPostMessageToPiano(iframeId, true, 'Please check your email to confirm your subscription.');
            }
            else if (data.status === 'subscribed') {
              // Set a cookie we can use in Composer.
              Cookies.set('_pc_nbp', 'sub', {expires: 7365, path: '/'});
              sendPostMessageToPiano(iframeId, true, 'You\'re signed up, thanks!');
            }
          })
          .fail(function (jqXHR, textStatus, errorThrown) {
            sendPostMessageToPiano(iframeId, false, 'Something went wrong (failed request). Please try again or contact us.');
          })
          .always(function (data, textStatus, jqXHR) {
            if (data.invalid_email) {
              sendPostMessageToPiano(iframeId, false, data.invalid_email, 'email');
            }
          });
        }
      }]);
    }]);

    /**
     * Function to send data upstream to Piano template
     * @param iframeId - Element id of the Piano iframe
     * @param success - true/false. Status of the message
     * @param message - Error message text
     * @param object - Additional data, like the the field that triggered the error
     */
    function sendPostMessageToPiano(iframeId, success, message, object) {
      var iframe = jQuery('#' + iframeId);

      if (iframe.length) {
        iframe[0].contentWindow.postMessage({
          piano: {
            success: success,
            message: message,
            object: object
          }
        }, '*');
      }
    }
  }
};


